(() => {
  const MOBILE = 767;
  const TABLET = 1023;

  window.isMobile = function () {
    return window.matchMedia(`(max-width: ${MOBILE}px)`).matches;
  };

  window.isTablet = function () {
    return window.matchMedia(`(max-width: ${TABLET}px)`).matches;
  };

  window.isDesktop = function () {
    return window.matchMedia(`(min-width: ${TABLET + 1}px)`).matches;
  };

  window.isMobileSafari = () => {
    const ua = window.navigator.userAgent;
    const iOS = !!ua.match(/iP(ad|od|hone)/i);
    const webkit = !!ua.match(/WebKit/i);
    return iOS && webkit && !ua.match(/CriOS/i) && !ua.match(/OPiOS/i);
  };
})();

(() => {
  const welcome = document.querySelector(`.welcome`);

  if (!welcome) {
    return;
  }

  const contracts = welcome.querySelectorAll(`.contract__item`);
  if (contracts.length > 1) {
    welcome.classList.add("old");
  };

})();
(() => {
  const form = document.querySelector(`.passport-form`);
  const btn = document.querySelector(`.passport-form button`);

  if (!form) {
    return;
  }

  btn.addEventListener("click", function() {
    form.classList.add("submitted");
  });



})();

$(function() {
  $("#number").mask("9999 999999",{placeholder:" "});
  $("#code").mask("999-999",{placeholder:" "});
  $("#postcode").mask("999999",{placeholder:" "});
});